mapper.select:
select * from 
	card_entity where 1=1

		and create_user_id = #{createUserId}


		and num = #{num}


		and batch_name = #{batchName}


		and batch_id = #{batchId}


		and hospital_id = #{hospitalId}


		and status = #{status}


		and active_time = #{activeTime}


		and check_time = #{checkTime}


		and valid_time_start = #{validTimeStart}


		and valid_time_end = #{validTimeEnd}

SELECT a.id, a.check_card_num AS checkCardNum, a.treat_card_num AS treatCardNum, a.name , a.mobile, ce.create_time AS createTime, ce.active_time AS activeTime, CASE ce.status WHEN 2 THEN "已检测" ELSE "未检测" END AS statusName, ce.check_time AS checkTime, a.check_office_name AS checkOficeName, CASE a.treat_card_status WHEN 1 THEN "开启" ELSE "关闭" END AS treatCardStatus, ( SELECT cp.name FROM patient_package_asso ppa LEFT JOIN check_package cp ON ppa.package_id = cp.id WHERE ppa.patient_item_id = a.id ORDER BY ppa.create_time DESC limit 1 ) AS checkPackageName FROM patient_item a INNER JOIN card_entity ce ON a.check_card_num = ce.num WHERE a.hospital_id =#{pd.hospitalId} AND a.check_card_num IS NOT NULL AND a.check_card_type=1
AND (a.check_card_num like concat('%',#{pd.queryKey},'%') OR a.name like concat('%',#{pd.queryKey},'%'))
AND a.mobile =#{pd.mobile}
AND( ce.check_time IS NOT NULL AND date_format(ce.check_time,'%Y-%m-%d')>=#{pd.checkTimeStart})
AND (ce.check_time IS NOT NULL AND date_format(ce.check_time,'%Y-%m-%d')<=#{pd.checkTimeEnd})
AND a.check_office_id =#{pd.officeId}
AND a.check_office_id IN (SELECT office_id FROM hospital_depart_office_asso WHERE department_id=#{pd.departmentId})
AND ce.status =#{pd.checkStatus}
AND a.treat_card_status =#{pd.treatStatus}
ORDER BY ce.check_time desc, ce.active_time desc, ce.create_time DESC
