mapper.select:
select * from 
	card_treatment where 1=1

		and create_user_id = #{createUserId}


		and num = #{num}


		and batch_name = #{batchName}


		and batch_id = #{pd.batchId}


		and hospital_id = #{hospitalId}


		and status = #{status}


		and active_time = #{activeTime}


		and check_time = #{checkTime}


		and valid_time_start = #{validTimeStart}


		and valid_time_end = #{validTimeEnd}

SELECT a.id, a.treat_card_num AS treatCardNum, a.check_card_num AS checkCardNum, a.name , a.mobile, b.create_time AS createTime, b.active_time AS activeTime, CASE b.status WHEN 2 THEN "已检测" WHEN 1 THEN "已激活" ELSE "未激活" END AS statusName, b.check_time AS checkTime, a.check_office_name AS checkOficeName, CASE a.treat_card_status WHEN 1 THEN "开启" ELSE "关闭" END AS treatCardStatus, cp.name AS checkPackageName FROM patient_package_asso ppa LEFT JOIN patient_item a ON ppa.patient_item_id = a.id LEFT JOIN check_package cp ON ppa.package_id = cp.id INNER JOIN card_treatment b ON a.treat_card_num = b.num WHERE a.hospital_id =#{pd.hospitalId} AND a.check_card_num IS NOT NULL
AND (a.treat_card_status like concat('%',#{pd.queryKey},'%') OR a.name like concat('%',#{pd.queryKey},'%'))
AND a.mobile =#{pd.mobile}
AND (b.check_time IS NOT NULL AND date_format(b.check_time,'%Y-%m-%d')>=#{pd.checkTimeStart})
AND (b.check_time IS NOT NULL AND date_format(b.check_time,'%Y-%m-%d')<=#{pd.checkTimeEnd})
AND a.check_office_id =#{pd.officeId}
AND a.check_office_id IN (SELECT office_id FROM hospital_depart_office_asso WHERE department_id=#{pd.departmentId})
AND b.status =#{pd.checkStatus}
AND a.treat_card_status =#{pd.treatStatus}
ORDER BY b.check_time desc, b.active_time desc, b.create_time DESC
