mapper.select:
select * from 
	card_virtual where 1=1

		and create_user_id = #{createUserId}


		and num = #{num}


		and batch_name = #{batchName}


		and hospital_id = #{hospitalId}


		and package_mix_id = #{packageMixId}


		and patient_id = #{patientId}


		and status = #{status}


		and active_time = #{activeTime}


		and check_time = #{checkTime}

SELECT a.id, a.check_card_num AS checkCardNum, a.treat_card_num AS treatCardNum, a.name , a.mobile, b.create_time AS createTime, b.active_time AS activeTime, CASE b.status WHEN 0 THEN "未激活" WHEN 1 THEN "已激活" ELSE "已检测" END AS statusName, b.check_time AS checkTime, a.check_office_name AS checkOficeName, CASE a.treat_card_status WHEN 1 THEN "开启" ELSE "关闭" END AS treatCardStatus, ( SELECT cp.name FROM patient_package_asso ppa LEFT JOIN check_package cp ON ppa.package_id = cp.id WHERE ppa.patient_item_id = a.id ORDER BY ppa.create_time DESC limit 1 ) AS checkPackageName FROM patient_item a LEFT JOIN card_virtual b ON a.check_card_num = b.num WHERE a.hospital_id =#{pd.hospitalId} AND a.check_card_num IS NOT NULL AND a.check_card_type=2
AND (a.check_card_num like concat('%',#{pd.queryKey},'%') OR a.name like concat('%',#{pd.queryKey},'%'))
AND a.mobile =#{pd.mobile}
AND (b.check_time IS NOT NULL AND date_format(b.check_time,'%Y-%m-%d')>=#{pd.checkTimeStart})
AND (b.check_time IS NOT NULL AND date_format(b.check_time,'%Y-%m-%d')<=#{pd.checkTimeEnd})
AND a.check_office_id =#{pd.officeId}
AND a.check_office_id IN (SELECT office_id FROM hospital_depart_office_asso WHERE department_id=#{pd.departmentId})
AND b.status =#{pd.checkStatus}
AND a.treat_card_status =#{pd.treatStatus}
ORDER BY b.check_time desc, b.active_time desc, b.create_time DESC
