mapper.select:
		select * from 
			check_package where 1=1

				and create_user_id = #{createUserId}


				and name = #{name}


				and price = #{price}


				and description = #{description}


				and office_id = #{officeId}


				and hospital_id = #{hospitalId}


				and doctor_id = #{doctorId}


                and root = #{root}


				and type_id = #{typeId}

select * from check_package a where 1=1
and a.create_time >= #{pd.query_time_start}
and a.create_time <= #{pd.query_time_end}
and a.name like concat('%',#{pd.name},'%')
and a.office_id like concat('%',#{pd.officeId},'%')
order by a.create_time desc

select * from 
	check_package
where 1=1 
and hospital_id = #{condition.hospitalId}
limit #{currentResult}, #{pageSize}

		select ma.package_id   
		from check_package_mix m 
		left join check_package_mix_package_asso ma on ma.package_mix_id = m.id 
		left join hospital_group_team t on m.group_team_id = t.id 	
		where m.batch_id = #{batchId} 

            and t.department_id = #{departmentId}


            and m.group_team_id = #{teamId}

select count(*) from 
	check_package a 
left join p_system_package_type b on a.type_id = b.id 
where 1=1 

	and a.hospital_id = #{hospitalId}


	and (a.type_id = #{typeId} or b.parent_id = #{typeId})


	and a.root = #{classify}

	and a.doctor_id = #{doctorId}


	and a.on_sold = 1

	select a.id, a.name, a.description, a.price 
	from 
		check_package a 
	left join p_system_package_type b on a.type_id = b.id  
	where 1=1 

		and a.hospital_id = #{condition.hospitalId}


		and (a.type_id = #{condition.typeId} or b.parent_id = #{condition.typeId})


		and a.root = #{condition.classify}

		and a.doctor_id = #{condition.doctorId}


		and a.on_sold = 1 


    order by a.create_time desc
	limit #{currentResult}, #{pageSize}
