mapper.select:
select * from 
	check_package_mix_package_asso where 1=1

		and create_user_id = #{createUserId}


		and package_mix_id = #{packageMixId}


		and package_id = #{packageId}


		and sort_index = #{sortIndex}

select * from 
		check_package_mix_package_asso a where 1=1

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc

select c.id, c.name, c.price, c.description from check_package_mix_package_asso a 
left join check_package c on a.package_id = c.id 
where a.package_mix_id = #{packageId}

select c.id from check_package_mix_package_asso a 
left join check_package c on a.package_id = c.id 
where a.package_mix_id = #{packageId}
