mapper.select:
select * from 
	check_package_mix_sheet_asso where 1=1

		and create_user_id = #{createUserId}


		and package_mix_id = #{packageMixId}


		and sheet_id = #{sheetId}


		and sort_index = #{sortIndex}

select * from 
		check_package_mix_sheet_asso a where 1=1

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc

select s.id, s.name, s.description from check_package_mix_sheet_asso a 
left join check_sheet s on a.sheet_id = s.id 
where a.package_mix_id = #{packageId} 
and s.id not in (
     select cs.id from check_package_mix_package_asso pp 
     left join check_package_sheet_asso ps on pp.package_id = ps.package_id  
     left join check_sheet cs on ps.sheet_id = cs.id 
     where pp.package_mix_id = #{packageId}
)

select s.id from check_package_mix_sheet_asso a 
left join check_sheet s on a.sheet_id = s.id 
where a.package_mix_id = #{packageId} 
and s.id not in (
     select cs.id from check_package_mix_package_asso pp 
     left join check_package_sheet_asso ps on pp.package_id = ps.package_id  
     left join check_sheet cs on ps.sheet_id = cs.id 
     where pp.package_mix_id = #{packageId}
)
