mapper.select:
select * from 
	patient_check_report_factor where 1=1

		and create_user_id = #{createUserId}


		and patient_item_id = #{patientItemId}


		and package_id = #{packageId}


		and factor_id = #{factorId}


		and name = #{name}


		and suggest = #{suggest}


		and remark = #{remark}


		and sheet_id = #{sheetId}


		and question_id_str = #{questionIdStr}


		and option_id_str = #{optionIdStr}

select * from 
		patient_check_report_factor a where 1=1

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc

select a.name factorName  
from patient_check_report_factor a 
left join p_member_order b on a.patient_item_id = b.patient_item_id 
where b.id is not null and b.member_id = #{memberId} 

and b.package_id = #{packageId}  


group by a.name
