mapper.select:
select * from 
	hospital_batch where 1=1

		and create_user_id = #{createUserId}


		and group_id = #{groupId}


		and number = #{number}


		and status = #{status}


		and remark = #{remark}


		and check_card_type = #{checkCardType}


		and entry_mode = #{entryMode}

select * from 
		hospital_batch a where 1=1

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc

        select count(*) from hospital_batch a 
        left join hospital_batch_department_asso da on a.id = da.batch_id
        where a.group_id = #{condition.groupId} 

				and a.number like concat('%', #{condition.keyword}, '%')


				and da.department_id = #{condition.departmentId}


				and da.team_id = #{condition.teamId}

        select a.id batchId, a.group_id groupId, a.status, a.number, a.check_card_type checkCardType, a.entry_mode entryMode, a.create_time createTime 
        from hospital_batch a 
        left join hospital_batch_department_asso da on a.id = da.batch_id 
        where a.group_id = #{condition.groupId} 

				and a.number like concat('%', #{condition.keyword}, '%')


				and da.department_id = #{condition.departmentId}


				and da.team_id = #{condition.teamId}

		group by a.id
		order by a.create_time desc 
		limit #{currentResult}, #{pageSize}
