mapper.select:
select a.*, d.name departmentName from 
	hospital_group_team a 
	left join hospital_group_department d on a.department_id = d.id 
	where 1=1

		and a.create_user_id = #{createUserId}


		and a.department_id = #{departmentId}


		and a.name = #{name}


		and a.account = #{account}


		and a.password = #{password}

select * from 
		hospital_group_team a where 1=1

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc
