mapper.select:
select * from 
	hospital_office where 1=1

		and create_user_id = #{createUserId}


		and hospital_id = #{hospitalId}


		and name = #{name}


		and account_name = #{accountName}


		and docking_name = #{dockingName}


		and docking_phone = #{dockingPhone}

select a.*,b.name 'hospital_name' from hospital_office a left join hospital b on b.id = a.hospital_id where 1=1
and a.create_time >= #{pd.query_time_start}
and a.create_time <= #{pd.query_time_end}
and b.name like concat('%',#{pd.name},'%')
and a.id IN (SELECT office_id FROM hospital_depart_office_asso WHERE department_id =#{pd.departmentId})
order by a.create_time desc

SELECT id,name FROM hospital_office WHERE 1=1

    AND hospital_id=#{hospitalId}


    AND id=#{officeId}


    AND id IN (SELECT office_id FROM hospital_depart_office_asso WHERE department_id=#{departmentId})
