mapper.select:
select * from 
	patient_package_asso where 1=1

		and create_user_id = #{createUserId}


		and patient_item_id = #{patientItemId}


		and package_id = #{packageId}


		and package_name = #{packageName}


		and package_price = #{packagePrice}


		and package_description = #{packageDescription}


		and sheet_id_str = #{sheetIdStr}


		and sheet_name_str = #{sheetNameStr}


		and check_card_num = #{checkCardNum}


		and status = #{status}

select * from 
		patient_package_asso a where 1=1

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc
