mapper.select:
select * from 
	p_doctor_speciality_asso where del_flag=0

		and `doctor_id` = #{doctorId}


		and `speciality_id` = #{specialityId}


		and `font_color_id` = #{fontColorId}

select * from 
		p_doctor_speciality_asso a where del_flag=0

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by doctor_id desc

mapper.delete:
		delete from p_doctor_speciality_asso
		where id in

                 #{item}
