mapper.select:
	select a.*, b.name type, b.icon_url iconUrl from 
		p_member_message a 
    left join p_system_message_type b on a.type_id = b.id
    where a.del_flag=0

			and a.`member_id` = #{memberId}


			and a.`type_id` = #{typeId}


			and a.`synopsis` = #{synopsis}


			and a.`content` = #{content}


			and a.`status` = #{status}


			and a.`order_id` = #{orderId}

		order by a.create_time desc

select a.*, b.name type from p_member_message a left join p_system_message_type b on a.type_id = b.id where a.del_flag=0
and a.create_time >= #{pd.query_time_start}
and a.create_time <= #{pd.query_time_end}
and a.name like concat('%',#{pd.query_key},'%')
order by a.create_time desc

mapper.delete:
		delete from p_member_message
		where id in

                 #{item}

mapper.select:
select a.*, b.name type, b.icon_url iconUrl, c.doctor_id doctorId from p_member_message a 
left join p_system_message_type b on a.type_id = b.id 
left join p_member_order c on a.order_id = c.id 
where a.del_flag=0 and a.member_id = #{condition.memberId} 
order by a.create_time desc 
limit #{currentResult}, #{pageSize}
