mapper.select:
select * from 
	p_system_version where del_flag=0

		and `name` = #{name}


		and `version` = #{version}


		and `type` = #{type}


		and `file_down_address` = #{fileDownAddress}


		and `qrcode_down_address` = #{qrcodeDownAddress}


		and `update_hint` = #{updateHint}


		and `update_date` = #{updateDate}


		and `status` = #{status}


		and `size` = #{size}


		and `is_constraint` = #{isConstraint}


		and `version_type` = #{versionType}

select * from 
		p_system_version a where del_flag=0

	and a.create_time >= #{pd.query_time_start}


	and a.create_time <= #{pd.query_time_end}


	and a.name like concat('%',#{pd.query_key},'%')

	order by a.create_time desc

mapper.delete:
		delete from p_system_version
		where id in

                 #{item}
